import React, { Component } from 'react';

class Mobile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      events: [
        {
          title: 'event 1',
          address: '1101 W. McKinley Ave. Pomona, CA 91768',
          photo: '../event1',
          vendors: [
            {
              title: 'vendor 1',
              photo: '../vendor1',
              items: [
                {
                  title: "water",
                  photo: "../water.gif",
                  price: "$1",
                  description: "drink it"
                }
              ]
            }
          ]
        },
        {
          title: 'event 2',
          address: '1101 W. McKinley Ave. Pomona, CA 91768',
          photo: '../event2',
          vendors: [
            {
              title: 'vendor 2',
              photo: '../vendor2',
              items: [
                {
                  title: "water",
                  photo: "../water.gif",
                  price: "$1",
                  description: "drink it"
                }
              ]
            }
          ]
        }
      ],
      event: [],
      title: [],
      address: []
    }
  };




  render() {

    const events = this.state.menus.map((menu, index) => {
      return (

        <div key={index} onClick={() => this.loadMenu(menu.title)} className="menu">
          <img alt={menu.title} src={menu.photo} />
          <p className="menuTitle">{menu.title}</p>
        </div>
      )
    });

    const vendors = this.state.items.map((item, index) => {
      return (<div key={index} className="section">
        <p className="sectionTitle">{this.state.title}</p>
        <div className="rule"></div>
        <div className="items">
          <div className="item">
            <img alt="menu item" src={item.photo} />
            <p className="itemTitle">{item.title}</p>
          </div>
        </div>
      </div>)
    });

    const menus = this.state.menus.map((menu, index) => {
      return (

        <div key={index} onClick={() => this.loadMenu(menu.title)} className="menu">
          <img alt={menu.title} src={menu.photo} />
          <p className="menuTitle">{menu.title}</p>
        </div>
      )
    });

    const items = this.state.items.map((item, index) => {
      return (<div key={index} className="section">
        <p className="sectionTitle">{this.state.title}</p>
        <div className="rule"></div>
        <div className="items">
          <div className="item">
            <img alt="menu item" src={item.photo} />
            <p className="itemTitle">{item.title}</p>
          </div>
        </div>
      </div>)
    });




    return (
      <div className="App">
        {events}
      </div>
    );
  }
}


export default Mobile;
