import React, { Component } from 'react';
import AddMenu from '../addmenu/addmenu';
import AddItem from '../additem/additem';
import EventSelect from '../eventselect/eventselect';

class Builder extends Component {
  constructor(props) {
    super(props);
    this.state = {
      events: [
        "LA County Fair",
        "Off the Grid",
        "Eat Drink Vegan",
        "Knotts Berry Farm",
        "Pokemon League Finals"
      ],
      attending: [
        "Off the Grid",
        "Eat Drink Vegan",
        "Pokemon League Finals"
      ],
      menus: [
        {
          title: "Bowls",
          photo: "../menu.jpg",
          items: [
            {
              title: "burger",
              photo: "../burritobowl.jpg",
              price: "",
              description: ""
            }
          ]
        },
        {
          title: "Drinks",
          photo: "../drinks.jpg",
          items: [
            {
              title: "water",
              photo: "../water.gif",
              price: "",
              description: ""
            }
          ]
        }
      ],
      title: [],
      items: []
    };
  }

  loadMenu = (menuTitle) => {
    let index = this.state.menus.findIndex(function (menu) {
      return menu.title === menuTitle
    });
    this.setState({ items: this.state.menus[index].items, title: this.state.menus[index].title })
  };

  deleteEvent = (eventTitle) => {

    var array = this.state.attending.filter(function (s) { return s != eventTitle });
    this.setState({ attending: array });

    let index = this.state.events.findIndex(function (menu) {
      return menu.title === eventIndex
    });
    this.setState({ attending: this.state.menus[index].items, title: this.state.menus[index].title })
  };


  render() {

    const menus = this.state.menus.map((menu, index) => {
      return (

        <div key={index} onClick={() => this.loadMenu(menu.title)} className="menu">
          <img alt={menu.title} src={menu.photo} />
          <p className="menuTitle">{menu.title}</p>
        </div>
      )
    });

    const items = this.state.items.map((item, index) => {
      return (<div key={index} className="section">
        <p className="sectionTitle">{this.state.title}</p>
        <div className="rule"></div>
        <div className="items">
          <div className="item">
            <img alt="menu item" src={item.photo} />
            <p className="itemTitle">{item.title}</p>
          </div>
        </div>
      </div>)
    });

    const existingEvents = this.state.attending.map((event, index) => (
      <span className="attendingTag" value={event}>{event}<img onClick={() => this.deleteEvent(event)} src="../close.svg" /></span>
    ));

    return (
      <div className="App" >
        <div id="toolbar">
          <AddMenu />|
          <AddItem />
          <EventSelect events={this.state.events} attending={this.state.attending} />
        </div>
        <div id="content">
          <div className="attendingEvents">
            <h3 className="attendingTitle">My Events: </h3>
            {existingEvents}
          </div>
          <div id="menus">
            {menus}
          </div>
          {items}
        </div>
      </div >

    )
  };
}

export default Builder;
