import React, { Component } from 'react';

class Mobile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      events: [
        {
          title: 'event 1',
          address: '1101 W. McKinley Ave. Pomona, CA 91768',
          photo: '../event1.jpg',
          vendors: [
            {
              title: 'vendor 1',
              photo: '../vendor1',
              items: [
                {
                  title: "water",
                  photo: "../water.gif",
                  price: "$1",
                  description: "drink it"
                }
              ]
            }
          ]
        },
        {
          title: 'event 2',
          address: '1101 W. McKinley Ave. Pomona, CA 91768',
          photo: '../event2.jpg',
          vendors: [
            {
              title: 'vendor 2',
              photo: '../vendor2',
              items: [
                {
                  title: "water",
                  photo: "../water.gif",
                  price: "$1",
                  description: "drink it"
                }
              ]
            }
          ]
        }
      ],
      vendors: [],
      title: [],
      address: []
    }
  };

  loadVendors = (eventTitle) => {
    let index = this.state.events.findIndex(function (event) {
      return event.title === eventTitle;
    });
    this.setState({ vendors: this.state.events[index].vendors })
  };

  // loadMenus = (emenuTitle) => {
  //   let index = this.state.events.findIndex(function (event) {
  //     return event.title === eventTitle;
  //   });
  //   this.setState({ vendors: this.state.events[index].vendors })
  // };




  render() {

    const events = this.state.events.map((event, index) => {
      return (

        <div key={index} onClick={() => this.loadVendors(event.title)} className="mEvent">
          <img alt={event.title} src={event.photo} />
          <p className="mEventTitle">{event.title}</p>
          <p className="mEventAddress">{event.address}</p>
        </div>
      )
    });

    const vendors = this.state.vendors.map((vendor, index) => {
      return (
        <div key={index} onClick={alert('hi')} className="mEvent">
          <img alt={vendor.title} src={vendor.photo} />
          <p className="mEventTitle">{vendor.title}</p>
        </div>)
    });

    // const menus = this.state.menus.map((menu, index) => {
    //   return (

    //     <div key={index} onClick={() => this.loadMenu(menu.title)} className="menu">
    //       <img alt={menu.title} src={menu.photo} />
    //       <p className="menuTitle">{menu.title}</p>
    //     </div>
    //   )
    // });

    // const items = this.state.items.map((item, index) => {
    //   return (<div key={index} className="section">
    //     <p className="sectionTitle">{this.state.title}</p>
    //     <div className="rule"></div>
    //     <div className="items">
    //       <div className="item">
    //         <img alt="menu item" src={item.photo} />
    //         <p className="itemTitle">{item.title}</p>
    //       </div>
    //     </div>
    //   </div>)
    // });




    return (
      <div id="mobileContent">
        {events}
        {vendors}
      </div>
    );
  }
}


export default Mobile;
