import React, { Component } from 'react';

class EventSelect extends Component {
  constructor(props) {
    super(props);

    this.state = {
      attending: '',
    };
  }

  newEvent(e) {
    this.setState({ selected: e.target.value });
  }

  render() {
    const existingEvents = this.props.events.map(event => (
      <option value={event} selected={this.state.attending === event}>{event}</option>
    ));

    return (
      <select name="select" onChange={this.num}>
        {existingEvents}
      </select>
    );
  }
}
export default EventSelect;
