import React, { Component } from 'react';

class EventSelect extends Component {
  constructor(props) {
    super(props);

    this.state = {
      selected: '',
    };
  }

  newEvent(e) {
    this.setState({ selected: e.target.value });
  }

  render() {
    return (

      <select name="select" onChange={this.newEvent}>
        {this.props.events.map(function (n) {
          return (<option value={n} selected={this.state.selected === n}>{n}</option>);
        })}
      </select>


    );
  }
}
export default EventSelect;
