import React, { Component } from 'react';

class Builder extends Component {

  constructor(props) {
    super(props);

    this.state = {
      menus: [
        {
          title: "food",
          photo: "../event.png",
          items: [
            {
              title: "burger",
              photo: "../burritobowl.jpg",
              price: "",
              description: ""
            }
          ]
        }
      ]
    };
  }



  loadMenu = () => {
    let menuIndex = event.target.value;
    console.log(menuIndex);
    console.log(event.target);

  };

  render() {

    const menus = this.state.menus.map((menu, index) => {
      return (<div key={index} id="content">
        <span>
          <div id="menus">
            <div onClick={() => this.loadMenu(menu.title)} className="menu">
              <img alt="menu item" src={menu.photo} />
              <p className="menuTitle">{menu.title}</p>
            </div>
          </div>
        </span>
      </div>)
    });

    const items = this.state.menus[0].items.map((item, index) => {
      return (<div key={index} className="section">
        <p className="sectionTitle">menu title</p>
        <div className="rule"></div>
        <div className="items">
          <div className="item">
            <img alt="menu item" src={item.photo} />
            <p className="itemTitle">{item.title}</p>
          </div>
        </div>
      </div>)
    });

    return (
      <div className="App">

        <div id="toolbar">
          <span className="toolbarItem">
            + Menu
            </span>|
            <span className="toolbarItem">
            + Item
            </span>
        </div>
